sys_script_include.script:
/**
 * @file ToonConstants – Core constants for the Toon JSON encoder/decoder
 */

/**
 * ToonConstants
 * @global
 * @type {Object}
 */
var ToonConstants = (function () {
    'use strict';

    // #region List markers
    var LIST_ITEM_MARKER = '-';
    var LIST_ITEM_PREFIX = '- ';
    // #endregion

    // #region Structural characters
    var COMMA = ',';
    var COLON = ':';
    var SPACE = ' ';
    var PIPE = '|';
    var HASH = '#';
    // #endregion

    // #region Brackets and braces
    var OPEN_BRACKET = '[';
    var CLOSE_BRACKET = ']';
    var OPEN_BRACE = '{';
    var CLOSE_BRACE = '}';
    // #endregion

    // #region Literals
    var NULL_LITERAL = 'null';
    var TRUE_LITERAL = 'true';
    var FALSE_LITERAL = 'false';
    // #endregion

    // #region Escape characters
    var BACKSLASH = '\\';
    var DOUBLE_QUOTE = '"';
    var NEWLINE = '\n';
    var CARRIAGE_RETURN = '\r';
    var TAB = '\t';
    // #endregion

    // #region Delimiters
    var DELIMITERS = Object.freeze({
        comma: COMMA,
        tab: TAB,
        pipe: PIPE
    });

    /**
     * @typedef {'comma'|'tab'|'pipe'} DelimiterKey
     */

    /**
     * @typedef {','|'\t'|'|'} Delimiter
     */

    var DEFAULT_DELIMITER = DELIMITERS.comma;
    // #endregion


    // --------------------------------------------------------------------
    // Public API – expose all constants on the returned object
    // --------------------------------------------------------------------
    return Object.freeze({
        LIST_ITEM_MARKER: LIST_ITEM_MARKER,
        LIST_ITEM_PREFIX: LIST_ITEM_PREFIX,

        COMMA: COMMA,
        COLON: COLON,
        SPACE: SPACE,
        PIPE: PIPE,
        HASH: HASH,

        OPEN_BRACKET: OPEN_BRACKET,
        CLOSE_BRACKET: CLOSE_BRACKET,
        OPEN_BRACE: OPEN_BRACE,
        CLOSE_BRACE: CLOSE_BRACE,

        NULL_LITERAL: NULL_LITERAL,
        TRUE_LITERAL: TRUE_LITERAL,
        FALSE_LITERAL: FALSE_LITERAL,

        BACKSLASH: BACKSLASH,
        DOUBLE_QUOTE: DOUBLE_QUOTE,
        NEWLINE: NEWLINE,
        CARRIAGE_RETURN: CARRIAGE_RETURN,
        TAB: TAB,

        DELIMITERS: DELIMITERS,
        DEFAULT_DELIMITER: DEFAULT_DELIMITER,

        // Export types via JSDoc (no runtime value needed)
        DelimiterKey: true,
        Delimiter: true
    });
})();
